using System;
using System.Linq;

class Solution
{
    static void Main(string[] args)
    {
        string team1 = Console.ReadLine();
        string team2 = Console.ReadLine();

        var t1 = team1.Split(';').Sum(fg => float.Parse(fg));
        var t2 = team2.Split(';').Sum(fg => float.Parse(fg));

        var msg = "draw";
        if (t1 > t2)
            msg = "team1";
        else if (t2 > t1)
            msg = "team2";

        Console.WriteLine(msg);
    }
}
