import java.util.Scanner;//Note: code is not optimized nor clean, but works
class Solution {
  static double error=0.01;
  static double combine (char op, double a, double b)//combines two numbers into one using the operator
  {
    if(op==('+'))
    {
      a+=b;
    }
    else if(op==('-'))
    {
      a-=b;
    }
    else if(op==('*'))
    {
      a*=b;
    }
    else if(b!=0)
    {
      a=a/b;
    }
    else
    {
      return -1000000;//bail out instead of trying to calculate division by 0
    }
    return a;
  }
  static double convertInt (String str, double a, double b, double c, double d)//converts a list of the operators and number order to the finished amount
  {
    if(str.equals("/-/") && a==8.0 && b==3.0 && c==3.0 && d==8.0)
    {
      System.err.println("Combo Found");
    }
    double sum=a;
    sum=combine(str.charAt(0),sum,b);
    sum=combine(str.charAt(1),sum,c);
    sum=combine(str.charAt(2),sum,d);
    if(Math.abs(sum)>(24-error) && Math.abs(sum)<(24+error))
    {
      System.err.println("Case a");
      return(sum);
    }
    double sum1=combine(str.charAt(0),a,b);
    double sum2=combine(str.charAt(2),c,d);
    sum=combine(str.charAt(1),sum1,sum2);
    if(Math.abs(sum)>(24-error) && Math.abs(sum)<(24+error))
    {
      System.err.println("Case b");
      return(sum);
    }
    sum=combine(str.charAt(1),sum2,sum1);
    if(Math.abs(sum)>(24-error) && Math.abs(sum)<(24+error))
    {
      System.err.println("Case b2");
      return(sum);
    }
    sum=combine(str.charAt(0),a,b);
    sum=combine(str.charAt(1),c,sum);
    sum=combine(str.charAt(2),d,sum);
    if(Math.abs(sum)>(24-error) && Math.abs(sum)<(24+error))
    {
      System.err.println("Case c");
      return(sum);
    }
    if(Math.abs(sum)>(24-error) && Math.abs(sum)<(24+error))
    {
      sum=combine(str.charAt(0),a,b);
      sum=combine(str.charAt(1),c,sum);
      sum=combine(str.charAt(2),sum,d);
      if(Math.abs(sum)>23.9 && Math.abs(sum)<24.1)
      {
        System.err.println("Case d");
        return(sum);
      }
    }
    if(Math.abs(sum)>(24-error) && Math.abs(sum)<(24+error))
    {
      sum=combine(str.charAt(0),a,b);
      sum=combine(str.charAt(1),sum,c);
      sum=combine(str.charAt(2),d,sum);
      if(Math.abs(sum)>23.9 && Math.abs(sum)<24.1)
      {
        System.err.println("Case e");
        return(sum);
      }
    }
    return(sum);
  }
  static boolean possible (double a, double b, double c, double d)//checks if a solution of 24 can be found with this particular order of numbers
  {
    String[] symbols;
    symbols=new String[4];
    boolean poss=false;
    symbols[0]="+";
    symbols[1]="-";
    symbols[2]="*";
    symbols[3]="/";
    for (int i=0;i<4;i++)
    {
      for (int j=0;j<4;j++)
      {
        for (int k=0;k<4;k++)
        {
          String symb=symbols[i]+symbols[j]+symbols[k];
          double converted=convertInt(symb,a,b,c,d);
          if(Math.abs(converted)>(24-error) && Math.abs(converted)<(24+error))
          {
            System.out.println("true");
            System.err.println(symb+" "+a+" "+b+" "+c+" "+d);
            poss=true;
          }
          if(poss)
          {
            break;
          }
        }
        if(poss)
        {
          break;
        }
      }
      if(poss)
      {
        break;
      }
    }
    return (poss);
  }
  public static void main(String[] args) {
    Scanner in = new Scanner(System.in);
    double[] digits;
    digits = new double[4];
    for (int i = 0; i < 4; i++) {
        int N = in.nextInt();
        System.err.println(N);
        digits[i]=N;
    }
    boolean done=false;
    for (int i=0;i<4;i++)
    {
        for (int j=0;j<4;j++)
        {
            for (int k=0;k<4;k++)
            {
                for (int l=0;l<4;l++)//run through every single combination of numbers
                {
                    if(i==j || i==k || i==l || j==k || j==l || k==l)//eliminate cases where it is calling the same position from the array (duplicate)
                    {
                        continue;
                    }
                    else {
                        if(possible(digits[i],digits[j],digits[k],digits[l]))//checks if possible
                        {
                        done=true;
                        }
                    }
                    if(done)//stops running if solution found
                    {
                        break;
                    }
                }
                if(done)
                {
                break;
                }
            }
            if(done)
            {
            break;
            }
        }
        if(done)
        {
            break;
        }
    }
    if(done==false)//prints out false if no solution was found
    {
        System.out.println("false");
    }
  }
}
