#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
#include <iterator>

using namespace std;

int main(int argc, const char * argv[]) {
    string key,message;
    getline(cin, key);
    getline(cin, message);

    int N=key[0]-'0';
    vector<size_t> transpose(N);
    transform(begin(key)+1, end(key), begin(transpose), [](auto a){return a-'1';});

    string msg;
    copy_if(begin(message), end(message), back_inserter(msg), [](auto c){ return isalnum(c);});
    for (auto&c:msg)
        c=toupper(c);
    int last=msg.size()%N;
    if (last){
        msg.reserve(msg.size()+N-last);
        auto p=begin(msg);
        auto q=end(msg);
        for( ;last<N; last++){
            msg+=*p++;
            if (p==q)
                p=begin(msg);
        }
    }

    string result(msg.size(),' ');
    for (int i=0; i<msg.size(); i+=N) {
        for (int j=0; j<N; j++)
            result[i+transpose[j]]=msg[i+j];
    }

    for (int i=0; i<result.size(); i+=N) 
        cout<<(i?" ":"")<<result.substr(i,N);
    cout<<endl;
}
