board = list()
result = list()
# read board and prepare result with valid positions everywhere
for i in range( 8 ):
    board.append( list( raw_input() ) )
    result.append( list( 'P' * 8 ) )

# look for queens in original board    
for row in range( 8 ):
    for col in range( 8 ):
        if board[row][col] != 'Q':
            continue
        # eliminate positions threatened by queens
        for i in range( 8 ):
            # up and down
            result[ max( 0, row - i ) ][ col ] = '.'
            result[ min( 7, row + i ) ][ col ] = '.'
            # left and right
            result[ row ][ max( 0, col - i ) ] = '.'
            result[ row ][ min( 7, col + i ) ] = '.'
            # upper diagonals
            if row -i >= 0:
                if col - i >= 0:
                    result[ row - i ][ col - i ] = '.'
                if col + i < 8:
                    result[ row - i ][ col + i ] = '.'
            # lower diagonals
            if row + i < 8:
                if col - i >= 0:
                    result[ row + i ][ col - i ] = '.'
                if col + i < 8:
                    result[ row + i ][ col + i ] = '.'


for r in result:
    print ''.join( r )
