x, y = map(int, input().split())
n = int(input())
emergencies = []
other_alarms = []
for i in range(n):
    alarm = input().split()
    xi=int(alarm[0])
    yi=int(alarm[1])
    distance = ((x-xi)**2 + (y-yi)**2)**.5
    a = {"x":int(alarm[0]), "y":int(alarm[1]), "d":distance}
    if len(alarm)>2:
        emergencies.append(a)
    else:
        other_alarms.append(a)

if len(emergencies):
    emergencies.sort(key=lambda a: a["d"],reverse=True)
    print("distance:",round(emergencies[-1]["d"],1))
else:
    other_alarms.sort(key=lambda a: a["d"],reverse=True)
    print("distance:",round(other_alarms[-1]["d"],1))
