n = int(input())
s = input()
count = {'A':0,'O':0}
marker_count = {'A':0,'O':0}
for i in range(n):
    count[s[i]]+=1

for i in range(n - 1):
    count[s[i]] -= 1
    marker_count[s[i]] += 1
    if count['A'] != marker_count['A'] and count['O'] != marker_count['O'] :
        print(i+1)
        exit(0)

print(-1)
