n = int(input())

data = []
for i in range(n):
    node = input()
    data.append(node)

def generate_tree(i):
    split_data = data[i].split(' ')

    if split_data[0] == 'leaf':
        return ' '.join(split_data[1::])

    return (generate_tree(int(split_data[1])), generate_tree(int(split_data[2])))

tree = generate_tree(0)

def concate_tree_leaves(tree):
    if type(tree) is str:
        return tree
    else:
        return concate_tree_leaves(tree[0])+concate_tree_leaves(tree[1])

print(concate_tree_leaves(tree))
