def solve1():
    """
    dynamic programming solution
    """
    a, b = map(int, input().split())
    n = int(input())

    MAX_X = 10_000

    dp = [False] * (MAX_X + 1)
    dp[0] = True # volume 0 is possible to measure

    for i in range(MAX_X + 1):
        if dp[i]:
            if i + a <= MAX_X:
                dp[i + a] = True
            if i + b <= MAX_X:
                dp[i + b] = True

    for _ in range(n):
        x = int(input())
        if dp[x]:
            print("POSSIBLE")
        else:
            print("IMPOSSIBLE")


def solve2():
    """
    One by one, try using the small beaker multiple times to get the answer.
    """
    a, b = map(int, input().split())
    n = int(input())

    for _ in range(n):
        x = int(input())
        num_a = 0

        possible = False

        while True:
            volume_a = a * num_a
            volume_b = x - volume_a
            if volume_a > x or volume_b < 0:
                break

            if volume_b % b == 0:
                possible = True
                break

            num_a += 1

        if possible:
            print("POSSIBLE")
        else:
            print("IMPOSSIBLE")


if __name__ == "__main__":
    solve2()
