import java.io.File;
import java.io.FileNotFoundException;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Scanner;
import java.util.regex.Pattern;

enum Place{ INDEFINIE, VIDE, ADULTE, ENFANT, MOI;
	@Override
	public String toString() {
		if(this == INDEFINIE) return "D";
		if(this == VIDE) return "D";
		if(this == ADULTE) return "A";
		if(this == ENFANT) return "k";
		if(this == MOI) return "x";
		else return "N/A";
	}
}

class Wagon {
	public final static int NBSIEGEPARWAGON = 10;	
	Siege[] sieges = new Siege[NBSIEGEPARWAGON];

	public Wagon() {
		super();		
		for(int i=0; i< Wagon.NBSIEGEPARWAGON;i++) {
			sieges[i] = new Siege();
		}
	}

	boolean isFull() {
		boolean r = true;
		for(int i=0; i< Wagon.NBSIEGEPARWAGON;i++) {
			if(sieges[i].gauche.equals(Place.INDEFINIE)) r = false;
		}
		return r;
	}

	int getNbPlacesLibresRestantes() {
		int resultat = 0;
		for(int i=0; i< Wagon.NBSIEGEPARWAGON;i++) {
			if(sieges[i].gauche.equals(Place.INDEFINIE))resultat++;
			if(sieges[i].droite.equals(Place.INDEFINIE))resultat++;
		}
		return resultat;
	}

	@Override
	public String toString() {
		return Arrays.toString(sieges);
	}
}


class Siege {
	Place gauche;
	Place droite;
	public Siege() {
		super();
		this.gauche = Place.INDEFINIE;
		this.droite = Place.INDEFINIE;
	}
	@Override
	public String toString() {
		return "Siege [gauche=" + gauche + ", droite=" + droite + "]";
	}	
}

class Train {
	ArrayList<Wagon> wagons = new ArrayList();
	public Train() {
		super();
		this.wagons.add(new Wagon());
	}	
}

public class Solution {	
	public static void main(String args[]) throws FileNotFoundException {				
	    Scanner in = new Scanner(System.in);
	    int n = in.nextInt();
	    if (in.hasNextLine()) {
            in.nextLine();
        }

	    Train train = new Train();	    
	    boolean jeSuisLa = false;
	    boolean monWagonEstPlein = false;
	    String line="";
	    for(int j=0;j<1;j++) {	    		    
	    	 line = in.nextLine();
	    	//String g = in.next();
	    }
	    String[] groupes = line.split(" ");
	    for(String g:groupes) {
	    	if(!monWagonEstPlein) {
		    	char[] charArray = g.toCharArray();
		        int nbAdultes = 0;
		        int nbEnfants = 0;
		        int moi = 0;
		        for(int i=0; i < charArray.length; i++){
		        	if( charArray[i] == 'x') moi++;
		        	else if( Character.isUpperCase( charArray[i] )) nbAdultes ++;
		            else nbEnfants++;
		        }
		        if (nbAdultes >= nbEnfants) {  	
		        	mettreLesGensDansTrain(train, nbAdultes, nbEnfants, moi);	        	
		        }
		        Wagon w = train.wagons.get(train.wagons.size() - 1);			
				if( w.isFull() && jeSuisLa) {
					monWagonEstPlein=true;
				}
				if(moi>0)jeSuisLa = true;
	    	}
	    }
	    afficheResultat(train);
	}

    private static final Pattern RTRIM = Pattern.compile("\\s+$");
    private static final String EMPTY_STRING = "";


    public static String rtrim(String str) {
        return RTRIM.matcher(str).replaceAll(EMPTY_STRING);
    }


	private static void afficheResultat(Train train) { 
		ArrayList<Wagon> wagons = train.wagons;
		//for(Wagon w: wagons) {
		Wagon w = wagons.get(wagons.size() - 1);
		Siege[] s = w.sieges;
		String gauche = "";
		String droite = "";
		for(int i=0; i< Wagon.NBSIEGEPARWAGON;i++) {
			gauche += s[i].gauche+" | ";
			droite += s[i].droite+" | ";
		}
		System.out.println(wagons.size());
		System.out.println("/< | " + rtrim(gauche));
		System.out.println("\\< | " + rtrim(droite));

	}

	private static void mettreLesGensDansTrain(Train train, int nbAdultes, int nbEnfants, int moi) {
		int nbPersonnesDansGroupe = nbAdultes + nbEnfants + moi;
		ArrayList<Wagon> wagons = train.wagons;
		boolean impaire=((nbAdultes+nbEnfants+moi)%2 == 0)?false:true;
		// on prend le dernier wagon
		Wagon w = wagons.get(wagons.size() - 1);
		// on regarde s'il reste des places dispo et que le nb de personnes groupe < nbplaces dispo
		if( w.isFull() || w.getNbPlacesLibresRestantes() < nbPersonnesDansGroupe ) {
			w = new Wagon();
			wagons.add(w);
		}

		Siege[] s = w.sieges;
		// Je m'assoie en premier
		for(int i=0; i< Wagon.NBSIEGEPARWAGON && moi>0;i++) {
			if( s[i].droite.equals(Place.INDEFINIE) && moi>0 ) {
				s[i].droite = Place.MOI;
				moi--;
			}
		}
		// on met tout les enfants d'abord
		for(int i=0; i< Wagon.NBSIEGEPARWAGON && nbEnfants>0;i++) {
			if( s[i].droite.equals(Place.INDEFINIE) && nbEnfants>0 ) {
				s[i].droite = Place.ENFANT;
				nbEnfants--;
			}
		}
		// on met les parents à coté des enfants
		for(int i=0; i< Wagon.NBSIEGEPARWAGON && nbAdultes>0;i++) {
			if( s[i].gauche.equals(Place.INDEFINIE) && nbAdultes>0 ) {
				s[i].gauche = Place.ADULTE;
				nbAdultes--;
			}
			if( s[i].droite.equals(Place.INDEFINIE) && nbAdultes>0 ) {
				s[i].droite = Place.ADULTE;
				nbAdultes--;
			}			
		}

		for(int i=0; (i< Wagon.NBSIEGEPARWAGON && impaire);i++) {
			if( s[i].gauche.equals(Place.ADULTE) && s[i].droite.equals(Place.INDEFINIE) ) {
				s[i].droite = Place.VIDE;
				impaire = false;
			}
		}	
	}
}
