#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

int main()
{
    char word[15], bestWord[15], msg[300][15];
    int i, j, a, max, c, shift = 0;
    bool b;
    for(i = 0; scanf("%s", word) == 1; i++){
        for(j = a = 0; word[j]; j++){
            if((word[j] | 32) >= 'a' && (word[j] | 32) <= 'z') msg[i][j-a] = ((word[j] | 32) - ++shift%26 + 7)%26+97;
            else if (word[j+1] && msg[i][j-1] && j) {i++; a = j+1;}
            else a++;
        }
        msg[i][j-a] = 0;
    }

    for(j = 0; j < i; j++){
        for(c = 0; msg[j][c]; c++) word[c] = msg[j][c];
        word[c] = 0;
        shift = 0;
        for(a = j; a < i; a++){
            b = true;
            for(c = 0; word[c]; c++){
                if(word[c] != msg[a][c]) {b = false; break;}
            }
            if (msg[a][c]) b = false;
            if(b) shift++;
        }
        if (shift > max){
            for(c = 0; msg[j][c]; c++) bestWord[c] = msg[j][c];
            bestWord[c] = 0;
            max = shift;
        }
    }
    printf("%s", bestWord);

    return 0;
}
