#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

void rec(vector<string>& substrings, string s, int n, int k,string r){
    if(k>n)
        return;
    if(k==0){
        substrings.push_back(r);
        return;
    }
    for(int i = 0;i<n;++i)
        rec(substrings, s,i,k-1,s[i]+r);
}
int main()
{

    string s;
    getline(cin, s);
    int k;
    cin >> k; cin.ignore();

    vector<string> substrings;
    rec(substrings,s,s.length(),k,"");

    sort( substrings.begin(), substrings.end() );
    substrings.erase( unique( substrings.begin(), substrings.end() ), substrings.end() );
    for(string t : substrings)
      cout << t << endl;

}
