data.solution:
n = int(input())
sequence = list(map(int, input().split(" ")))

replacements = []
for num in sequence:
    if num % n == 0:
        replacements += [num]

replacements.sort()

ret = []
i = 0
for num in sequence:
    if num % n == 0:
        ret += [replacements[i]]
        i += 1
    else:
        ret += [num]

print("".join(f"{num} " for num in ret)[:-1])
