rabbits, foxes, n = [int(i) for i in input().split()]
t = [int(i) for i in input().split()]

for i in range(n) :
    if 12 * foxes <= rabbits:
        rabbits -= foxes * 3
        foxes = int(foxes * 1.2)
    else: 
        rabbits = max(0,rabbits -foxes)
        foxes = int(foxes * 0.9)
    if t[i] >= 20 and t[i] <= 35:
        rabbits *= 2
    else :
        rabbits = int(rabbits * 0.9)

print(rabbits, foxes)
