data.solution:
import java.util.*;

class Solution {

  public static void main(String[] args) {
    Scanner in = new Scanner(System.in);
    int N = in.nextInt();
    int i = in.nextInt();
    int x = in.nextInt();

    int numOfDiagonals = (N*(N-3))/2 + N;
    int inkNeeded = numOfDiagonals*x;

    if(inkNeeded > i){
      System.out.println("no");
      System.out.println(inkNeeded-i);
    } else {
      System.out.println("yes");
      System.out.println(inkNeeded);
    }
  }
}

