data.solution:
equation = input().split()
all_values = {}
constants = int(input())
for i in range(constants):
    value = input().split(' = ')
    all_values[value[0]] = int(value[1])
for fix in range(len(equation)):
    if equation[fix].isalpha():
        equation[fix] = all_values[equation[fix]]
    elif not(equation[fix].isnumeric()) and equation[fix] != '+' and equation[fix] != '-':
        equation[fix] = int(equation[fix][:-1]) * all_values[equation[fix][-1]]
answer = 0
plus_minus = '+'
for solve in equation:
    if solve == '+':
        plus_minus = '+'
    elif solve == '-':
        plus_minus = '-'
    elif plus_minus == '+':
        answer += int(solve)
    elif plus_minus == '-':
        answer -= int(solve)
print(answer)
