using System;

namespace CalculateAverageOfEvenNumbers
{
    class Program
    {
        static void Main(string[] args)
        {
            int[] numbers = new int[10];
            int sum = 0;
            int count = 0;

            for (int i = 0; i < 10; i++)
            {
                numbers[i] = Convert.ToInt32(Console.ReadLine());
                if (numbers[i] % 2 == 0)
                {
                    sum += numbers[i];
                    count++;
                }
            }

            if (count > 0)
            {
                double average = (double)sum / count;
                Console.WriteLine("The average of the even numbers is: {0}", average);
            }
            else
            {
                Console.WriteLine("No even numbers were entered.");
            }
        }
    }
}
