let chess = []
let plus = 0
let mult
const S = readline();
for(i=0; i<5; i++){
    chess.push(S.charAt(i)); 
}
chess.includes("K")? mult=200 : mult=100;

for(i=0; i<chess.length; i++){
    if(chess[i] == "Q"){
        plus+=9;
    } else if(chess[i] == "K"){
        plus+=4;
    } else if(chess[i] == "R"){
        plus+=5;
    } else if(chess[i] == "P"){
        plus+=1;
    } else if(chess[i] == "B" || chess[i] == "N"){
        plus+=3;
    }
}

console.log(plus*mult);
