using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        string[] inputs;
        inputs = Console.ReadLine().Split(' ');
        int explorerX = int.Parse(inputs[0]);
        int explorerY = int.Parse(inputs[1]);
        int maxDistance = int.Parse(Console.ReadLine());

        float minDist = 200;
        int index = 0;

        int N = int.Parse(Console.ReadLine());
        for (int i = 0; i < N; i++)
        {
            inputs = Console.ReadLine().Split(' ');
            int x = int.Parse(inputs[0]);
            int y = int.Parse(inputs[1]);
            float dist = (float)Math.Sqrt(Math.Pow(explorerX - x, 2) + Math.Pow(explorerY - y, 2));
            if (dist < minDist)
            {
                minDist = dist;
                index = i+1;
            }
        }

        if(minDist <= maxDistance)
        {
            Console.WriteLine(index);
        }
        else
        {
            Console.WriteLine("STRANDED");
        }
    }
}
