import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);

        int[] count = new int[10];
        for (long i = in.nextLong(); i > 0 ; i /= 10) {
            count[(int)(i%10L)]++;
        }
        for (int i=0; i < 10; i++) {
            if (count[i] > 0) System.out.print(count[i] + "" + i);
        }
    }
}
