const word = readline();
const waviness = parseInt(readline());
let output = [];
let rownum = waviness;
let dir = 1;

if(waviness === 0) {
    console.log(word);
} else {
    for(let i = 0; i < (waviness * 2) + 1; i++) {
        output.push([]);
    }

    [...word].forEach((ch, i) => {
        output[rownum]?.push(ch);

        for(let u = 0; u < output.length; u++) {
            if(u !== rownum) {
                output[u].push(" ");
            }
        }

        if(rownum === output.length - 1) {
            dir = -1;
        } else if(rownum === 0) {
            dir = 1;
        }

        rownum += dir;
    });

    console.log(output.map(row => row.join("")).join("\n"));
}
