#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

int main()
{
    // given inputs
    char top[4];
    scanf("%[^\n]", top); fgetc(stdin);
    char bottom[4];
    scanf("%[^\n]", bottom);
    // directions toward hazard
    char direction[4][6]={"South","North","West","East"};
    // outputs nulled
    char outA[6]={0},outB[6]={0};
    // top and bottom patterns
    char *t = "^v^v";
    char *b = "^vv^";
    char *p=top,*q=bottom;
    // iterate through top,bottom patterns and load outputs
    while(*p){
        for(int i=0;i<4;i++)
            if(*p==*(t+i)){
                for(int j=0;j<4;j++)
                    // check top and bottom both match
                    if(*q==*(b+j)&&j==i){
                        if(*outA)strcpy(outB,direction[j]);
                        else strcpy(outA,direction[j]);
                    }
            }
            p++;q++;
    }
    // output and check for two marks and reversed marks
    if(outB[0])
        printf("%s-%s\n",*outA=='W'||*outA=='E'?outB:outA,*outB=='S'||*outB=='N'?outA:outB);
    else
        printf("%s",outA);

    return 0;
}
