line=input()

c=input()

s=sorted([x for x in line.upper() if x.isalpha()])

d={}

for i in range(len(s)):
    if s[i] not in d: d[s[i]]=0
    d[s[i]]+=1


for i in range(max(d.values()),0,-1):
    r=''
    for j in d:
        if d[j]>i-1:
            r+=c
        else:
            r+=' '
    r=r.rstrip()
    print(r)

print(''.join(sorted(set(s))))
