import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        String shots = in.nextLine();

        char d = 'D';
        char w = 'W';
        char db = '*';

        String solution = "";
        char last = d; //random value to initialize
        for (int i = 0; i < shots.length(); i++) {
            if (shots.charAt(i) == d) {
                solution = solution + d;
                last = d;
            }
            else if (shots.charAt(i) == w) {
                solution = solution + w;
                last = w;
            }
            else if (shots.charAt(i) == db) {
                solution = solution + last + last;
            }
        }

        int cd = 0;
        int cw = 0;
        for (int i = 0; i < solution.length(); i++) {
            if (solution.charAt(i) == d) {
                cd += 1;
            }
            else if (solution.charAt(i) == w) {
                cw += 1;
            }
        }

        if (cd >= cw) {
            System.out.println(cw);
        }
        else {
            System.out.println(cd);
        }

        // Write an answer using System.out.println()
        // To debug: System.err.println("Debug messages...");
    }
}

