#include <iostream>
using namespace std;

int main() {
    int a[3];
    for (int i = 0; i < 3; i++) cin >> a[i];

    if (a[0] < a[1] && a[1] < a[2]) {
        cout << "sorted" << endl;
    } 
    else if (a[0] > a[1] && a[1] < a[2] && a[0] < a[2]) {
        cout << "s" << endl;
    } 
    else if (a[0] < a[1] && a[1] > a[2] && a[0] > a[2]) {
        cout << "rr" << endl;
    } 
    else if (a[0] > a[1] && a[1] < a[2] && a[0] > a[2]) {
        cout << "r" << endl;
    }
    else if ((a[0] < a[1] && a[1] > a[2] && a[0] < a[2]) || (a[0] > a[1] && a[1] > a[2])) {
        cout << "multiple minimal solutions exist" << endl;
    }
}
