using System;
using System.Linq;
using System.Collections.Generic;

string Input = Console.ReadLine();
List<int> time = Input.Split(':').Select(n => int.Parse(n)).ToList();
List<List<string>> StringsList = new();

for (int i = 0; i < time.Count; i++)
{
    if (i != 3 && i != 0) StringsList.Add(convertToBinList(time[i] / 10));
    StringsList.Add(convertToBinList(time[i] % 10));
}

var Output = "";
for (int i = 0; i < 4; i++)
{
    StringsList.ForEach(n => Output += n[i]);
    Output += i ==3? '|':"|\n";
}
Console.WriteLine(Output);

static List<string> convertToBinList(int N)
{
    string S = Convert.ToString(N, 2).PadLeft(4,'0');
    List<string> Buffer = new();
    foreach (var c in S) Buffer.Add(c == '0' ? "|_____" : "|#####");
    return Buffer;
}
