import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String message = in.nextLine();
        sablier(message);
    }
    private static void sablier(String s){
		if(s.length()%2 == 0) System.out.print("Error");
		else sablier(s,0);
	}
	private static void sablier (String s, int n){
		if(s.length() == 1){
			espace(n);
			System.out.println(s);
			return;
		}
		espace(n);
		System.out.println(s);
		sablier(s.substring(1, s.length()-1),n+1);
		espace(n);
		System.out.println(s);
	}

	private static void espace(int n){
		for(int i = 0; i<= n-1;i++){
			System.out.print(" ");
		}
	}
}

