using System;
using System.Collections.Generic; 

public class Program
{
	public const string bl = " ";
	public static Dictionary<string,char> catDic = new();
	public static string dash = "-";
	public static string dot = ".";
	public static string catdash = "Meow"; //medium 
	public static string catdot = "Mewl"; //short

	public static void Main()
	{
        string copiedFromStub = ".- -... -.-. -.. . ..-. --. .... .. .--- -.- .-.. -- -. --- .--. --.- .-. ... - ..- ...- .-- -..- -.-- --..";
        string AZ = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

        string morseAZ = copiedFromStub;
        string catAZ = morseAZ.Replace(dash,catdash).Replace(dot,catdot);
		var catAZray = catAZ.Split();

		foreach (char ch in AZ) // load dics
		{
			int index = AZ.IndexOf(ch);
			string catLtr = catAZray[index];
			catDic.Add(catLtr, ch);
		}		

        string catTalk = Console.ReadLine();        
        string ans = "";

        var catTalkRay = catTalk.Split();
        foreach (string aCatLtr in catTalkRay)
        {
            bool foundIt = catDic.ContainsKey(aCatLtr);
            if (foundIt)
            {
                ans += catDic[aCatLtr];
            }
            else
            {
                ans += bl;	
            }
        }
        ans = ans.Trim();
        Console.WriteLine(ans);	
	}
}
