using System;

public class Program
{
	public static char[,] Ray;
    public static char foregroundChar;
	public static void Main()
	{		
		int size = int.Parse(Console.ReadLine());
		Ray = new char[size,size];

        string charsAsStr= Console.ReadLine();
        char ch1 = charsAsStr[0];
		InitRay(ch1);

        foregroundChar = charsAsStr[1];

        string[] inputs = Console.ReadLine().Split(' ');

		int pointAx = int.Parse(inputs[0]);
        int pointAy = int.Parse(inputs[1]);
        int pointBx = int.Parse(inputs[2]);
        int pointBy = int.Parse(inputs[3]);
        int pointCx = int.Parse(inputs[4]);
        int pointCy = int.Parse(inputs[5]);
		DrawLine4(pointAx, pointAy, pointBx, pointBy );
        DrawLine4(pointBx, pointBy, pointCx, pointCy );
		PrintRay();
	}


	public static void DrawLine4 (int x, int y, int xx, int yy)	
	{
		Point PtA = new Point(x,y);
		Point PtZ = new Point(xx,yy);
		Point.DrawLine2(PtA,PtZ);
	}	

	public static void InitRay(char symbol)
    {
		int size =  Ray.GetLength(0);      
        for (int y = 0 ; y < size ; y++ )
        {
            for (int x = 0 ; x < size ; x++ )
            {
                Ray[x,y] = symbol;
            }
        }
    }

	public static void PrintRay()
    {
        int size =  Ray.GetLength(0);       
        for (int y = 0; y < size; y++)
        {
            string fullLine = "";
            for (int x = 0; x < size; x++)
            {
                fullLine += Ray[x,y];
            }
            Console.WriteLine(fullLine);
        }
    }

	public class Point
	{
        public int X; public int Y;

		public Point(int x, int y)
		{
            X = x; Y = y;
		}

		public static void DrawLine2(Point PtA, Point PtZ)
		{
			int aX = PtA.X;
            int aY = PtA.Y;

			int zX = PtZ.X;
			int zY = PtZ.Y;

			int howManyX = Math.Abs(zX - aX);
			int howManyY = Math.Abs(zY - aY);
			int dist = Math.Max(howManyX, howManyY);

			for (int i = 0 ; i <= dist ; i++)
			{
				int xDirection = 0; if (aX < zX) xDirection = 1; if (aX > zX) xDirection = -1;
				int yDirection = 0; if (aY < zY) yDirection = 1; if (aY > zY) yDirection = -1;

                int nextX = aX + i * xDirection;
                int nextY = aY + i * yDirection;

				Ray[nextX,nextY] = foregroundChar;
			}	
		}
	}
}
