def reversal(s):
    vowels = [char for char in s if char.lower() in 'aeiouy']
    str_list = list(s)
    vowels.reverse()
    vowel_index = 0
    for i in range(len(str_list)):
        if str_list[i].lower() in 'aeiouy':
            str_list[i] = vowels[vowel_index]
            vowel_index += 1
    return ''.join(str_list)

s = input().strip()
print(reversal(s))
