#include <stdio.h>

int main(){
    char message[256];
    scanf("%[^\n]",message);
    int i = 0;
    while (message[i]!='\0'){
        i++;
    }
    char offset = 65-message[i-7];
    for (int j = 0;j<i;j++){
        if (message[j]=='{')
            printf(" ");
        else
            printf("%c",message[j]+offset);
    }
    printf("\n");
}
