#include <stdio.h>

int main(){
    int offset;
    scanf("%d",&offset);getchar();
    char message[257];
    scanf("%[^\n]",message);
    int i = 0;
    while (message[i]!='\0'){
        i++;
    }
    for (int j = 0; j<i-8; j++){
        if (message[j]==' ')
            printf(" ");
        else
            printf("%c",*(message+j)-offset);        
    }
    printf("\n");
}

