d = {}

def find_all(word):
    word = word.capitalize()
    if word=="": return [""]
    if not word in d:
        l = []
        if word[:1] in e:
            for x in find_all(word[1:]):
                l.append(word[:1] + x)
        if len(word)>=2 and word[:2] in e:
            for x in find_all(word[2:]):
                l.append(word[:2] + x)
        d[word] = l
    return d[word]

