def decode(s):
    x=""
    l=""
    o=0
    if not all([i in "|~.*+-/%" for i in s]):
        return 'Syntax Error'
    for j in s:
        if j=="|":
            o+=1
        elif j=="~":
            if o!=0: x+=str(o)
            o=0
            x+="0"
        else:
            x+=str(o)+j
            o=0

        l=j
    if o!=0:
        x+=str(o)
    return x

s=input()
x=decode(s)
if x!="Syntax Error":
    print(x)
    y=eval(x)
    if int(y)==y:
        y=int(y)
    print(y)
else:
    print("Syntax Error")
