#include <stdio.h>

struct Operation
{
	char op;
	int num;
};

// Prioritize each operator
int priorities[255] =
{
	['+'] = 1,
	['/'] = 2,
	['-'] = 3,
	['*'] = 4
};

struct Operation operations[20];
int opCount;

int getOperation( char* outOp, int* outNum )
{
	if ( opCount )
	{
		struct Operation* curOp = &operations[--opCount];
		*outOp = curOp->op;
		*outNum = curOp->num;
		return 1;
	}

	return scanf( "%c%d", outOp, outNum ) == 2;
}

int eval( int num1, char op, int num2 )
{
	switch ( op )
	{
	case '+': return num1 + num2;
	case '/': return num1 / num2;
	case '-': return num1 - num2;
	case '*': return num1 * num2;
	default: return 0;
	}
	return 0;
}

int recursiveEval( int leftSide, char curOperator, int rightSide )
{
	char op;
	int num;

	if ( getOperation( &op, &num ) )
	{
		if ( priorities[curOperator] <= priorities[op] )
		{
			leftSide = eval( leftSide, curOperator, rightSide );
			curOperator = op;
			rightSide = num;
		}
		else
		{
			rightSide = recursiveEval( rightSide, op, num );
		}
		struct Operation* curOp = &operations[opCount++];
		curOp->op = curOperator;
		curOp->num = rightSide;

		return leftSide;
	}

	return eval( leftSide, curOperator, rightSide );
}

int main()
{
	int leftSide = 0;
	int rightSide = 0;
	char curOperator = 0;

	// Read in the first integer
	scanf( "%d", &leftSide );

	// Read in the next operator and number if they exist
	while ( getOperation( &curOperator, &rightSide ) )
	{
		leftSide = recursiveEval( leftSide, curOperator, rightSide );
	}

	printf( "%d\n", leftSide );
}
