// Read the input value of n from the standard input
const n = +readline();

/** 
 * Define a function to calculate the sum of squares up to n.
 * @Formula: n * (n + 1) / 2
 */
const sumOfFirstNaturalNumbers = (n) => n * (n + 1) / 2;

/**
 * Define the main function to calculate the alternating square sum
 * 
 * @Explain: With any number n is EVEN, the result is always negative 
 * of the sum of first n natural numbers.
 * 
 * @Prove: 1^2 - 2^2 + ... + (n-1)^2 + n^2 
 * <=> (1 + 2)*(1 - 2) +...+ [(n-1) + n]*[(n-1) - n]
 * <=> -(1 + 2) + ... + -[(n-1) + n]
 * <=> -[1 + 2 + 3 + ... + (n-1) + n]
 * <=> -1 * [n * (n + 1) / 2]
 */
const alternatingSquareSum = (n) => {
    // If n is odd, -1 * sumOfFirstNaturalNumbers(n - 1) gives the sum of squares of even numbers
    // Then, we add n^2 to get the final sum of alternating squares
    if (n % 2) {
        return -1 * sumOfFirstNaturalNumbers(n - 1) + n * n;
    }
    // If n is even, -1 * sumOfFirstNaturalNumbers(n) gives the final sum of alternating squares.
    else {
        return -1 * sumOfFirstNaturalNumbers(n);
    }
};

// Log the result of the alternating square sum for the given value of n
console.log(alternatingSquareSum(n));


