var inputs = readline().split(' ')
const X = parseInt(inputs[0])
const Y = parseInt(inputs[1])

const arr = []
for (let i = 0; i < Y; i++) {
    const temp = readline().split('')
    if (temp[0] === '~')
        break
    arr.push(temp)
}

for (let y = 0; y < arr.length; y++) {
    for (let x = 0; x < X; x++) {
        if (arr[y][x] !== ' ')
            break
        arr[y][x] = ','
    }
    for (let x = X - 1; x >= 0; x--) {
        if (arr[y][x] !== ' ')
            break
        arr[y][x] = ','
    }
}

const map2str = arr.map((a) => a.join('')).join('\n')
const nbOfSpace = map2str.match(/ /g)?.length || 0
console.log(nbOfSpace)
