#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// Note that structs are ever-so-slightly slower than normal adjacency lists
// But with the constraints it doesn't matter
struct node {
    // For each node (person) in our tree (the family tree), keep track of all its children 
    // so we can jump to them when from the current node
    vector<int> children;
};

// Graph representing the family tree
vector<node> graph;
int children = 0;

// Recursive DFS
// We start from our parent node and keep dropping down the tree and keep count of all the children
void dfs(int curIdx) {
    children++;
    node cur = graph[curIdx];

    for (int next : cur.children) {
        dfs(next);
    }
}

int main()
{
    int n;
    cin >> n;

    // Initialize our graph
    for (int i = 0; i < n + 1; i++) {
        node cur;
        graph.push_back(cur);
    }

    // Create the edges from the parent to the child for our DFS
    // We do x-1, y-1, and p-1 because the input is one-indexed, while languages are 0-indexed
    for (int i = 0; i < n; i++) {
        int x, y;
        cin >> x >> y;
        graph[x - 1].children.push_back(y - 1);
    }

    int p;
    cin >> p;
    dfs(p - 1);

    cout << (children - 1); // We have to subtract one because we also counted the start node in our search
}
