def get_row_and_col(board, player):
    # Check rows
    for i, row in enumerate(board):
        if row.count(player) == 2 and row.count('.') == 1:
            col = row.index('.')
            return i, col

    # Check columns
    for col in range(3):
        column = [board[0][col], board[1][col], board[2][col]]
        if column.count(player) == 2 and column.count('.') == 1:
            row = column.index('.')
            return row, col

    # Check diagonals
    dia1 = board[0][0] + board[1][1] + board[2][2]
    dia2 = board[0][2] + board[1][1] + board[2][0]
    if dia1.count(player) == 2 and dia1.count('.') == 1:
        index = dia1.index('.')
        return index, index
    if dia2.count(player) == 2 and dia2.count('.') == 1:
        index = dia2.index('.')
        return index, 2 - index

    return None


def can_win_with_one_move(turn, board):
    player = turn
    return get_row_and_col(board, player)

turn = input().strip()
board = [input().strip() for _ in range(3)]

result = can_win_with_one_move(turn, board)
if result:
    print(f"{result[0]} {result[1]}")
else:
    print("Cannot win with one move.")
