const F = readline(); // the foreign expression: "word word word..."
const fwords = F.split(' ')
const dictionary = {
   "oombga": "plus",
   "dofta": "minus",
   "clebo": "multiplied-by",
   "nikta": "divided-by",
   "gumbro": "one",
   "abido": "two",
   "ulpika": "three",
   "noflu": "four",
   "vivast": "five",
   "lapri": "six",
   "ribiku": "seven",
   "poskul": "eight",
   "bowfu": "nine"
}

const numbers = []
const operations = []
let result = 0

const nums = {
    "one":1,"two":2,
    "three":3,"four":4,
    "five":5,"six":6,
    "seven":7,"eight":8,
    "nine":9
}

const ops = {
    "plus": function(a,b){return a+b},
    "minus": function(a,b){return a-b},
    "multiplied-by": function(a,b){return a*b},
    "divided-by": function(a,b){return a/b}
}

fwords.forEach((fword,i)=>{
    const word = dictionary[fword]
    if((i+1)%2==0){
        //even
        operations.push(word)
    }else{
        //odd
        numbers.push(nums[word])
        if(i==0){
            result = nums[word]
        }
    }
})

operations.forEach((op,i)=>{
    result = ops[op](result,numbers[i+1])
})

console.log(result)
