def set_pop(s):
    if not s:
        raise KeyError("pop from an empty set")

    temp_set = s.copy()
    element = temp_set.pop() 
    return element, temp_set  

def decrypt_with_wordlist(textwords, wordlist, possible_substitutions = {}):
    if not textwords:
        return possible_substitutions

    encrypted_word, ntextwords = set_pop(textwords)
    if not encrypted_word.replace("'",'').isalpha():
        return decrypt_with_wordlist(ntextwords, wordlist, possible_substitutions)

    for word in wordlist:
        if len(word) == len(encrypted_word):
            match = True
            local_substitution = {}
            for i in range(len(word)):
                if (encrypted_word[i] in possible_substitutions and possible_substitutions[encrypted_word[i]] != word[i]) or \
                   (encrypted_word[i] in local_substitution and local_substitution[encrypted_word[i]] != word[i]) or \
                   (word[i] in possible_substitutions.values() and encrypted_word[i] not in possible_substitutions) or \
                   (word[i] in local_substitution.values() and encrypted_word[i] not in local_substitution):
                    match = False
                    break
                local_substitution[encrypted_word[i]] = word[i]

            if match:
                rest_of_text_solve = decrypt_with_wordlist(ntextwords, wordlist, {**possible_substitutions, **local_substitution})
                if rest_of_text_solve is not None:
                    return rest_of_text_solve

    return None

def substitute_char(char, substitution_map):
    if char.upper() in substitution_map:
        if char.isupper():
            return substitution_map[char.upper()]
        else:
            return substitution_map[char.upper()].lower()
    else:
        return char

def decrypt(sentence, wordlist):
    for c in [',','.','!',':', '?',';']:
        sentence = sentence.replace(c, ' ')
    sentence = sentence.upper()

    return decrypt_with_wordlist(set(sentence.split()), wordlist)

sentence = input()
n = int(input())
words = {input() for i in range(n)}

solve_map = decrypt(sentence, words)

inv_solve_map = {b: a for a,b in solve_map.items()}

print(*map(lambda x: substitute_char(x, solve_map), sentence), sep='')

