use std::io;

macro_rules! parse_input {
    ($x:expr, $t:ident) => ($x.trim().parse::<$t>().unwrap())
}

fn main() {
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let m = parse_input!(input_line, i32);
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let s = parse_input!(input_line, i32);
    let mut input_line = String::new();
    io::stdin().read_line(&mut input_line).unwrap();
    let d = parse_input!(input_line, i32);

    let laps = d as f32 / 111.0;
    let time = ((m*60)+s) as f32 / laps;
    let speed = (111.0*3600.0)/(time*1000.0);

    println!("{}", (time*10.0).round()/10.0);
    println!("{}", speed.round());
}
