def decode(c, k):
    if c.isupper():
        return chr(ord('A')+(-ord(k)+ord('A')+ord(c)-ord('A'))%26)
    if c.islower():
        return chr(ord('a')+(-ord(k)+ord('A')+ord(c)-ord('a'))%26)
    return c
def uncrypt(key, message):
    i = 0
    lk = len(key)
    decrypted_message = ""
    for c in message:
        if c.isalpha():
            decrypted_message+=decode(c,key[i%lk])
            i+=1
        else:decrypted_message+=c
    return decrypted_message

