import sys
import math
n = int(input())
score1=0
score2=0
for i in range(n):
    penalty,goalkeeper= input().split()
    if penalty!=goalkeeper and penalty!='Miss':
        if i%2==0:
            score1+=1
        else:
            score2+=1
if score1>score2:
    print("1",str(score1)+"-"+str(score2))
else:
    print("2",str(score1)+"-"+str(score2))
