const N = parseInt(readline());
const alpha = "abcdefghijklmnopqrstuvwxyz".toUpperCase();
for (let i = 0; i < N; i++) {
    const name = readline();
    let isValid = true;
    for (let j = 0; j < name.length; j++) {
        if (!alpha.includes(name[j])) {
            console.log("INVALID");
            isValid = false;
            break;
        }
    }
    if (isValid) console.log("VALID");
}
