function sum(array) {
    S = 0

    for (valeur of array) {
        S += valeur
    }

    return S
}

function show(array) {
    let string = "" + array[array.length-1];

    for (let i = array.length - 2 ; i >= 0 ; i--) {
        string += (" "+array[i]);
    }
    console.log(string);

}


function rendu_graphe(register,goalAmount) {
    if (goalAmount == 0) {
        return [0];
    } else if (register.includes(goalAmount)) {
        return [goalAmount];
    }
    opened_set = []
    n_operations = 0

    for (coin of register) opened_set.push([coin]);

    previous_amounts = []

    while (true) {
        new_set = [];
        for (poss of opened_set) {
            for (coin of register) {
                new_poss = poss.concat([coin]);
                rendu = sum(new_poss);
                if (rendu == goalAmount) {
                    return new_poss;
                } else if (rendu < goalAmount && !previous_amounts.includes(rendu)) {
                    n_operations += 1
                    new_set.push(new_poss);
                    previous_amounts.push(rendu)
                }
            }
        }
        if (new_set.length == 0) {
            return "IMPOSSIBLE";
        }
        opened_set = new_set

    }
}

let register = readline();

register = register.split(" ");

for (let i = 0 ; i < register.length ; i ++) register[i] = parseInt(register[i]);

let goalAmount = parseInt(readline());

solution = rendu_graphe(register,goalAmount)

if (solution == "IMPOSSIBLE") {
    console.log(solution)
} else {
    show(solution)
}
