#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int count_group(string s)   {
    int res = 0, prev = 0;
    bool joker = false;

    for (char c : s)    {
        if (isspace(c))
            continue;
        if ((c == '2' && prev && prev != 'A') || (prev == '2' && c != '3'))
            joker = true;
        if ((isdigit(c) && c != '0') || isupper(c))
            res++;
        else if (c == 'o')
            joker = true;
        prev = c;
    }
    if (res >= 7 && joker)
        return (100);
    if (res == 14)
        return (1000);
    if (res == 13)
        return (500);
    if (res >= 7)
        return (200);
    return (0);
}

int count_cards(string s)   {
    int res = 0;

    for (char c : s)    {
        if (isspace(c))
            continue ;
        if (c == 'A')
            res += 15;
        else if (c == 'o')
            res += 10;
        else if (c >= '3' && c <= '7')
            res += 5;
        else if ((isdigit(c) && c != '0')|| isupper(c))
            res += 10;
    }
    return (res);
}

int main()
{
    int n, total;
    cin >> n; cin.ignore();
    for (int i = 0; i < n; i++) {
        string cards;
        getline(cin, cards);
        cerr << cards << endl << "Count cards " << count_cards(cards) << ". Count group " << count_group(cards) << endl;
        total += count_cards(cards) + count_group(cards);
    }

    cout << total << endl;
}
