N = int(input())
re = ""
for i in range(N):
    s = input()
    for ii in range(len(s)):
        if s[ii].isdigit() and len(s) != 1 and (s[ii-1].isdigit() or ii != len(s)-1 and s[ii+1].isdigit()):
            re += s[ii] * int(s[ii])
        elif (s[ii].islower()):
            re+= s[ii].upper()
        else:
            re += '*'

print(re)
