import java.util.*;

class Solution {
	static String known3 = "1101100";

	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		int order = in.nextInt();
		long S = in.nextLong();
		long E = in.nextLong();

		StringBuilder sb = new StringBuilder();
		for (long n = S; n <= E; n++) {
			sb.append(getEntry(order, n + 1));
		}

		System.out.println(sb.toString());
		in.close();
	}

	public static int getEntry(int order, long n) {
		if (n % 2 == 1) {
			long x = 2 & n;
			return (x == 2) ? 0 : 1;
		} else {
			while (n > 2) {
				n /= 2;
				if (n % 2 == 1) {
					long x = 2 & n;
					return (x == 2) ? 0 : 1;
				}
			}
			return known3.charAt((int) (n - 1)) - '0';
		}
	}
}
