import java.util.Scanner;

class Solution {
	
	public static void main(String[] args){
		Scanner in = new Scanner(System.in);
			String roman = in.nextLine();
			int dec = 0;
			for (int j = 0; j < roman.length(); j++){
				if (j == 0){
					switch(roman.charAt(j)){
					case 'I': dec += 1;
					break;
					case 'V': dec += 5;
					break;
					case 'X': dec += 10;
					break;
					case 'L': dec += 50;
					break;
					case 'C': dec += 100;
					break;
					case 'D': dec += 500;
					break;
					case 'M': dec += 1000;
					}
				} else {
					switch(roman.charAt(j)){
					case 'I': dec += 1;
					break;
					case 'V': 
						if (roman.charAt(j-1) == 'I'){
							dec += 3;
						} else {
							dec += 5;
						}
						break;
					case 'X':
						if (roman.charAt(j-1) == 'I'){ 
							dec += 8;
						} else {
							dec += 10;
						}
						break;
					case 'L':
						if (roman.charAt(j-1) == 'X'){
							dec += 30;
						} else {
							dec += 50;
						}
						break;
					case 'C':
						if (roman.charAt(j-1) == 'X'){
							dec += 80;
						} else {
							dec += 100;
						}
						break;
					case 'D':
						if (roman.charAt(j-1) == 'C'){
							dec += 300;
						} else {
							dec += 500;
						}
						break;
					case 'M':
						if (roman.charAt(j-1) == 'C'){
							dec += 800;
						} else {
							dec += 1000;
						}
						break;
					}
				}
			}
			System.out.println(dec);
	}
}
