import java.io.File;

import java.util.Scanner;
import java.util.StringTokenizer;


class Vlad extends Fight implements Fighter{
	public int punch() {return 3;}
	public int kick() {return 3;}
	public int special() {return 2;}
	public String getName() {return "VLAD";}
}

class Jade extends Fight implements Fighter{	
	public int punch() {return 2;}
	public int kick() {return 7;}
	public int special() {return 0;}
	public String getName() {return "JADE";}
}

class Jun extends Fight implements Fighter{	
	public int punch() {return 2;}
	public int kick() {return 1;}
	public int special() {return 1;}
	public String getName() {return "JUN";}
}

class Anna extends Fight implements Fighter{	
	public int punch() {return 9;}
	public int kick() {return 1;}
	public int special() {return 1;}
	public String getName() {return "ANNA";}
}

class Tank extends Fight implements Fighter{	
	public int punch() {return 2;}
	public int kick() {return 2;}
	public int special() {return 2;}
	public String getName() {return "TANK";}
}

class Ken extends Fight implements Fighter{
	public int punch() {return 6;}
	public int kick() {return 5;}
	public int special() {return 3;}
	public String getName() {return "KEN";}
}

class Ryu extends Fight implements Fighter{
	public int punch() {return 4;}
	public int kick() {return 5;}
	public int special() {return 4;}
	public String getName() {return "RYU";}
}
class Fight implements Fighter{
	int life=25;
	int rage=0;
	int nbHitGiven=0;
	void applyAction(Fight f, String action){		
		if( action.compareTo("KICK") == 0) {f.life -= kick();}
		if( action.compareTo("PUNCH") == 0) {f.life -= punch();}
		if( action.compareTo("SPECIAL") == 0) {
			if(getName().compareTo("VLAD") == 0) {
				f.life -= special()* (this.rage + f.rage); f.rage=0;
			} else if(getName().compareTo("JADE") == 0) {
				f.life -= nbHitGiven * rage;
			} else if(getName().compareTo("ANNA") == 0) {
				f.life -= (18-life) * rage;
			} else if(getName().compareTo("JUN") == 0) {
				f.life -= rage;
				life += rage;
			}else {
				f.life -= special()*rage; 
			}
			rage=0;
		}
		f.rage++;
		nbHitGiven++;		
	}
	@Override
	public int punch() {return 0;}
	@Override
	public int kick() {return 0;}
	@Override
	public int special() {return 0;}
	@Override
	public String getName() {return null;}

}
interface Fighter{
	public String getName();
	public int punch();
	public int kick();
	public int special();
}

public class Solution {
	public static void main(String args[]) {		
        Scanner in = new Scanner(System.in);
		String xst = in.nextLine();		
		StringTokenizer st = new StringTokenizer(xst);
		Fight f1=null,f2=null;
		String f1Name = st.nextToken();		
		if(f1Name.compareTo("RYU") == 0) {f1=new Ryu();}
		if(f1Name.compareTo("KEN") == 0) {f1=new Ken();}
		if(f1Name.compareTo("VLAD") == 0) {f1=new Vlad();f1.life=30;}
		if(f1Name.compareTo("ANNA") == 0) {f1=new Anna();f1.life=18;}
		if(f1Name.compareTo("JUN") == 0) {f1=new Jun();f1.life=60;}
		if(f1Name.compareTo("TANK") == 0) {f1=new Tank();f1.life=50;}
		if(f1Name.compareTo("JADE") == 0) {f1=new Jade();f1.life=20;}

		String f2Name = st.nextToken();
		if(f2Name.compareTo("RYU") == 0) {f2=new Ryu();}
		if(f2Name.compareTo("KEN") == 0) {f2=new Ken();}
		if(f2Name.compareTo("VLAD") == 0) {f2=new Vlad();f2.life=30;}
		if(f2Name.compareTo("ANNA") == 0) {f2=new Anna();f2.life=18;}
		if(f2Name.compareTo("JUN") == 0) {f2=new Jun();f2.life=60;}
		if(f2Name.compareTo("TANK") == 0) {f2=new Tank();f2.life=50;}
		if(f2Name.compareTo("JADE") == 0) {f2=new Jade();f2.life=20;}

		int H = in.nextInt();
		in.nextLine();

		for (int i=0; i < H; i++) {
        	String action = in.nextLine();        	
        	StringTokenizer strike = new StringTokenizer(action);
        	String direction = strike.nextToken();
        	String hit = strike.nextToken();
            if( direction.compareTo(">")==0){f1.applyAction(f2, hit);}else{f2.applyAction(f1, hit);}        	
        	if(f2.life <=0 || f1.life <=0)break;
		}

		if(f1.life <=f2.life ) System.out.println(f2.getName() +" beats "+ f1.getName()+" in "+f2.nbHitGiven+" hits");
		else System.out.println(f1.getName() +" beats "+ f2.getName() +" in "+f1.nbHitGiven+" hits");     
    }
}
